Trigger.script:
local logDate = matches[2] local desc = matches[3] local yearlyCredits = matches[4] local loggedActivity = { uniqueId = logDate.." ".."rewardAudit audit", timestamp = logDate, desc = desc, trackedType = "audit", names = "" }
if not fairySec:hasActivity(loggedActivity) then fairySec:addActivity(loggedActivity) end
if (fairySec.rewards.total.logDateStart ~= "" and fairySec.isRoughYearStart() and fairySec:getOSTime(logDate) > fairySec:getOSTime(fairySec.rewards.total.logDateStart) - fairySec.HOUR_IN_SECONDS and fairySec:getOSTime(logDate) < fairySec:getOSTime(fairySec.rewards.total.logDateStart) + fairySec.HOUR_IN_SECONDS) then fairySec.rewards.total.logDateStart = logDate fairySec:debug("setting logDateStart "..logDate) end if (fairySec.rewards.total.logDateEnd ~= "" and fairySec.isRoughYearEnd() and fairySec:getOSTime(logDate) > fairySec:getOSTime(fairySec.rewards.total.logDateEnd) - fairySec.HOUR_IN_SECONDS and fairySec:getOSTime(logDate) < fairySec:getOSTime(fairySec.rewards.total.logDateEnd) + fairySec.HOUR_IN_SECONDS) then fairySec.rewards.total.logDateEnd = logDate fairySec.rewards.total.yearlyCredits = tonumber(yearlyCredits) fairySec:debug("setting logDateEnd "..logDate) fairySec:debug("setting yearlyCredits "..logDate) elseif (fairySec.rewards.total.logDateEnd and not fairySec.isRoughYearEnd() and fairySec:getOSTime(logDate) > fairySec:getOSTime(fairySec.rewards.total.logDateEnd) - fairySec.HOUR_IN_SECONDS and fairySec:getOSTime(logDate) < fairySec:getOSTime(fairySec.rewards.total.logDateEnd) + fairySec.HOUR_IN_SECONDS) then -- presumes full end date is within log and both org and pointlog read raiseEvent("fairySecEndGatherLogs") end

-- fairySec:sendLogDate(matches[2]) -- convert date
local loggedPower = { timestamp = matches[2], trackedType = "power", desc = matches[4].." - "..fairySec.rewards.empower, names = matches[3] }
fairySec:addActivity(loggedPower)

ScriptGroup.script:
if not fairySec then
  fairySec = {}
end

Script.script:
--- org-specific rewards in terms of marks/credits fairySec.rewards = { ["timequake"] = 3, ["revolt"] = 5, ["aetherflare"] = 5, ["otherConquest"] = 5, ["domoth"] = fairySec.rewards.otherConquest, ["design"] = 15, ["prestige"] = 60, ["empower"] = 10, -- empowering the Aspects/Elemental Lords ["guild"] = 50, -- credits per guild ["total"] = { ["year"] = "", ["logDateStart"] = "", ["logDateEnd"] = "", ["yearlyCredits"] = 0, ["memberCredits"] = 0, ["guildCredits"] = 50 * 3, }, }
fairySec.orgPolicy = { ["name"] = "", ["leader"] = "", -- 'types' for key list in-order when iterating ["types"] = {"timequake", "conquest", "design", "culture", "power", "ikon"}, ["labels"] = { ["timequake"] = "Timequakes", ["conquest"] = "Conquest Activities", ["design"] = "Designs", ["culture"] = "Library and Stage", ["power"] = "Power", ["ikon"] = "Ikon Tournament" }, ["revolt"] = { "Stewartsville", "Estelbar", "Acknor", "Delport", "Rockholm", "Southgard", "Angkrag", "Dairuchi", "Paavik", "Shanthmark", "Ptoma", "Ixthiaxa", "Rikenfriez", "Talthos" }, ["revoltSet"] = { -- order within a set must match order in 'revolt' "Stewartsville/Delport", "Estelbar/Acknor", "Rockholm/Southgard/Angkrag/Ptoma/Ixthiaxa", "Dairuchi/Talthos", "Paavik/Shanthmark", "Rikenfriez", }, ["aetherflare"] = { "Bottledowns", "Cankermore", "Crumkindivia", "Dramube", "Facility", "Mucklemarsh", "Xion", "Frosticia" }, ["aetherflareSet"] = { -- order within a set must match order in 'aetherflare' "Bottledowns/Facility", "Cankermore/Dramube/Mucklemarsh", "Crumkindivia/Xion/Frosticia", } }
if not fairySec.records then fairySec.records = {} end
fairySec.recordSchema = { ["name"] = "", ["timequake"] = {}, ["domoth"] = {}, ["revolt"] = {}, ["aetherflare"] = {}, ["otherConquest"] = {}, ["design"] = {}, ["library"] = {}, ["stage"] = {}, ["power"] = {}, ["ikon"] = {}, ["subtotals"] = { ["timequake"] = 0, ["domoth"] = 0, ["conquest"] = 0, ["design"] = 0, ["culture"] = 0, ["power"] = 0, ["ikon"] = 0, } } -- each recorded activity under a type (timequake, domoth, etc.) is of schema -- { ["formattedDesc"] = string, ["reward"] = number }
-- additional saved data specific to org if not fairySec.db.orgCulture then fairySec.db.orgCulture = {} end if not fairySec.db.tallyError then fairySec.db.tallyError = {} end if not fairySec.db.tallyWarning then fairySec.db.tallyWarning = {} end

local function note(message, icon, color)
  if icon == nil then
    icon = " "
  end
  if color == nil then
    color = fairySec.config.color
  end
  cecho("\n"..color.."["..icon.."] "..message)  
end

function fairySec:warningMemberNotFound(name)
  return fairySec:warning("Member by name of "..name:title().." not found")
end

function fairySec:note(message, icon)
  return note(message, icon)
end

function fairySec:warning(message)
  return note(message, "?", fairySec.config.warningColor)
end

function fairySec:error(message)
  return note(message, "!", fairySec.config.errorColor)
end

function fairySec:debug(message)
  if fairySec.config.debug then
    debugc(message)
  end
end
