-- additional saved data specific to org if not fairySec.db.orgPowerIds then fairySec.db.orgPowerIds = {} end if not fairySec.db.orgCulture then fairySec.db.orgCulture = {} end if not fairySec.db.tallyError then fairySec.db.tallyError = {} end if not fairySec.db.tallyWarning then fairySec.db.tallyWarning = {} end

local function note(message, icon, color)
  if icon == nil then
    icon = " "
  end
  if color == nil then
    color = fairySec.config.color
  end
  cecho("\n"..color.."["..icon.."] "..message)  
end

function fairySec:warningMemberNotFound(name)
  return fairySec:warning("Member by name of "..name:title().." not found")
end

function fairySec:note(message, icon)
  return note(message, icon)
end

function fairySec:warning(message)
  return note(message, "?", fairySec.config.warningColor)
end

function fairySec:error(message)
  return note(message, "!", fairySec.config.errorColor)
end

function fairySec:debug(message)
  if fairySec.config.debug then
    debugc(message)
  end
end

-- Glomdoring custom function fairySec:addOrgPower(orgSummary) local orgPower = orgSummary:split("\t") local name, amount local timestamp = os.date("!%Y/%m/%d %H:%M:%S", getEpoch()) local orgPowerActivity = {}
for i, activity in ipairs(orgPower) do _, _, name, amount = string.find(activity, "^(%w+)%s+(%d+)")
if name ~=nil and amount ~=nil then local orgPowerActivity = { uniqueId = timestamp.." orgPower"..name.." power", timestamp = timestamp, trackedType = "power", desc = "Power - "..amount, names = name } fairySec:addActivity(orgPowerActivity, true) end end end
function fairySec:updateOrgPower(orgSummary) fairySec:deleteOrgPower() fairySec:addOrgPower(orgSummary) end
function fairySec:deleteOrgPower() -- remove previous power entries local trackedActivities = fairySec.db.log for _, activity in spairs(trackedActivities) do if string.find(activity.uniqueId:lower(), "orgpower") then fairySec:deleteActivity(activity.uniqueId, true) end end end -- end Glomdoring custom
