Trigger.script:
local logDate = matches[2] local desc = matches[3] local yearlyCredits = matches[4] local loggedActivity = { uniqueId = logDate.." ".."rewardAudit audit", timestamp = logDate, desc = desc, trackedType = "audit", names = "" }
local loggedActivityIkonParticipation = { uniqueId = logDate.." ".."ikonParticipation", timestamp = logDate, desc = "Participation in the Annual Ikon tournament", trackedType = "ikonParticipation", names = "" }
if not fairySec:hasActivity(loggedActivity) then fairySec:addActivity(loggedActivity) end if not fairySec:hasActivity(loggedActivityIkonParticipation) then fairySec:addActivity(loggedActivityIkonParticipation) end
if (fairySec.rewards.total.logDateStart ~= "" and fairySec.isRoughYearStart() and fairySec:getOSTime(logDate) > fairySec:getOSTime(fairySec.rewards.total.logDateStart) - fairySec.HOUR_IN_SECONDS and fairySec:getOSTime(logDate) < fairySec:getOSTime(fairySec.rewards.total.logDateStart) + fairySec.HOUR_IN_SECONDS) then fairySec.rewards.total.logDateStart = logDate fairySec:debug("setting logDateStart "..logDate) end if (fairySec.rewards.total.logDateEnd ~= "" and fairySec.isRoughYearEnd() and fairySec:getOSTime(logDate) > fairySec:getOSTime(fairySec.rewards.total.logDateEnd) - fairySec.HOUR_IN_SECONDS and fairySec:getOSTime(logDate) < fairySec:getOSTime(fairySec.rewards.total.logDateEnd) + fairySec.HOUR_IN_SECONDS) then fairySec.rewards.total.logDateEnd = logDate fairySec.rewards.total.yearlyCredits = tonumber(yearlyCredits) fairySec:debug("setting logDateEnd "..logDate) fairySec:debug("setting yearlyCredits "..logDate) elseif (fairySec.rewards.total.logDateEnd == logDate and not fairySec.isRoughYearEnd()) then -- presumes full end date is within log and both org and pointlog read raiseEvent("fairySecEndGatherLogs") end

local placeToType = 
{
  Winning = "ikonFirst",
  Second = "ikonSecond",
}

local loggedIkon = {
  uniqueId = matches[2].." "..placeToType[matches[4]],
  timestamp = matches[2],
  trackedType = placeToType[matches[4]],
  desc = desc,
  names = ""
}
fairySec:addActivity(loggedIkon)

ScriptGroup.script:
if not fairySec then
  fairySec = {}
end

