fairySec.orgPolicy = { ["name"] = "Serenwilde", ["leader"] = "Xiran", -- 'types' for key list in-order when iterating ["types"] = {"timequake", "conquest", "design", "culture", "power", "ikon"}, ["labels"] = { ["leadership"] = "Leadership", ["timequake"] = "Timequakes", ["conquest"] = "Conquest Activities", ["design"] = "Designs", ["culture"] = "Library and Stage", ["power"] = "Power", ["ikon"] = "Ikon Tournament" }, ["maxTimequakeReward"] = 36, ["revolt"] = { "Stewartsville", "Estelbar", "Acknor", "Delport", "Rockholm", "Southgard", "Angkrag", "Dairuchi", "Paavik", "Shanthmark", "Ptoma", "Ixthiaxa", "Rikenfriez", "Talthos" }, ["revoltSet"] = { -- order within a set must match order in 'revolt' "Stewartsville/Delport", "Estelbar/Acknor", "Rockholm/Southgard/Angkrag/Ptoma/Ixthiaxa", "Dairuchi/Talthos", "Paavik/Shanthmark", "Rikenfriez", }, ["aetherflare"] = { "Bottledowns", "Cankermore", "Crumkindivia", "Dramube", "Facility", "Mucklemarsh", "Xion", "Frosticia" }, ["aetherflareSet"] = { -- order within a set must match order in 'aetherflare' "Bottledowns/Facility", "Cankermore/Dramube/Mucklemarsh", "Crumkindivia/Xion/Frosticia", } }
if not fairySec.records then fairySec.records = {} end
fairySec.recordSchema = { ["name"] = "", ["leadership"] = {}, ["timequake"] = {}, ["domoth"] = {}, ["revolt"] = {}, ["aetherflare"] = {}, ["otherConquest"] = {}, ["design"] = {}, ["library"] = {}, ["stage"] = {}, ["power"] = {}, ["ikon"] = {}, ["subtotals"] = { ["timequake"] = 0, ["domoth"] = 0, ["conquest"] = 0, ["design"] = 0, ["culture"] = 0, ["power"] = 0, ["ikon"] = 0, } } -- each recorded activity under a type (timequake, domoth, etc.) is of schema -- { ["formattedDesc"] = string, ["reward"] = number }
-- additional saved data specific to org if not fairySec.db.orgPowerIds then fairySec.db.orgPowerIds = {} end if not fairySec.db.orgCulture then fairySec.db.orgCulture = {} end if not fairySec.db.tallyError then fairySec.db.tallyError = {} end if not fairySec.db.tallyWarning then fairySec.db.tallyWarning = {} end

local function note(message, icon, color)
  if icon == nil then
    icon = " "
  end
  if color == nil then
    color = fairySec.config.color
  end
  cecho("\n"..color.."["..icon.."] "..message)  
end

function fairySec:warningMemberNotFound(name)
  return fairySec:warning("Member by name of "..name:title().." not found")
end

function fairySec:note(message, icon)
  return note(message, icon)
end

function fairySec:warning(message)
  return note(message, "?", fairySec.config.warningColor)
end

function fairySec:error(message)
  return note(message, "!", fairySec.config.errorColor)
end

function fairySec:debug(message)
  if fairySec.config.debug then
    debugc(message)
  end
end
